mapper.update:
update song_list


    title = #{title,jdbcType=VARCHAR},


    pic = #{pic,jdbcType=VARCHAR},


    style = #{style,jdbcType=VARCHAR},


    introduction = #{introduction,jdbcType=LONGVARCHAR}


where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, title, pic, style, introduction
from song_list
where id = #{id,jdbcType=INTEGER}
