mapper.select:

select id, singer_id, name, introduction, create_time, update_time, pic, url, lyric
from song
where id = #{id,jdbcType=BIGINT}

select
id, singer_id, name, introduction, create_time, update_time, pic, url, lyric
from song where name like CONCAT('%',#{name},'%')
